import {
  getCachedContentCards,
  logCardDismissal,
  logContentCardClick,
  logContentCardImpressions,
  logCustomEvent,
  removeSubscription,
  requestContentCardsRefresh,
  subscribeToContentCardsUpdates,
} from '@braze/web-sdk'
import { init } from './braze'

// Other teams need access to the braze functions, but we don't want to import all of braze, so we are going to make our own custom export of the functions we want to expose.
const hasWindow = typeof window !== 'undefined'
if (hasWindow && !window.fsBraze) {
  window.fsBraze = {
    logCustomEvent,
  }
}

export {
  init as _init,
  getCachedContentCards,
  logCardDismissal,
  logContentCardClick,
  logContentCardImpressions,
  logCustomEvent,
  removeSubscription,
  requestContentCardsRefresh,
  subscribeToContentCardsUpdates,
}
