import React from 'react'
import { useTranslation } from 'react-i18next'
import { Link } from '@fs/zion-router'
import ErrorPage, { propTypes } from './ErrorPage'

export default function NotFoundError({ heading, body, ...rest }) {
  const [t] = useTranslation()

  return (
    <ErrorPage
      statusCode="404"
      title={t('error-page.not-found.title')}
      heading={heading || t('error-page.not-found.header')}
      body={body || t('error-page.not-found.body')}
      image="not-found"
      links={[
        <Link external to="/help/helpcenter/">
          {t('error-page.option-help-center')}
        </Link>,
        <Link external to="/search/">
          {t('error-page.option-records')}
        </Link>,
        <Link external to="/site-map">
          {t('error-page.option-site-map')}
        </Link>,
      ]}
      reloadButton
      {...rest}
    />
  )
}

NotFoundError.propTypes = propTypes
