import { useMemo } from 'react'
import { useUser } from '@fs/zion-user'
import Cache from '@fs/zion-cache'

export default function useSearchCache() {
  const { signedIn } = useUser()
  const db = signedIn ? null : 'fs'
  return useMemo(() => new Cache({ dbName: db, storeName: 'search', type: 'local', lifetime: 'hour' }), [db])
}
