import { Alert, type Button, InlineButton } from '@fs/zion-ui'
import React, { type ComponentProps } from 'react'
import { useTranslation } from 'react-i18next'

type Props = { onClick: ComponentProps<typeof Button>['onClick'] } & Partial<
  Omit<ComponentProps<typeof Alert>, 'children'>
>

export default function GedcomWarningAlert({ onClick, ...props }: Props): JSX.Element {
  const [t] = useTranslation()

  return (
    <Alert type="warning" outlined {...props}>
      {t('groups.gedcom-fail-warning.description')}{' '}
      <InlineButton data-testid="gedcom-fail-button" onClick={onClick}>
        {t('groups.gedcom-fail-warning.action')}
      </InlineButton>
    </Alert>
  )
}
