import { Toggle } from '@fs/zion-ui'
import React from 'react'
import type { ChangeEventHandler } from 'react'
import { useTranslation } from 'react-i18next'

type Props = {
  'data-testid'?: string
  onToggle: ChangeEventHandler<HTMLInputElement>
  isPreferredTreeSelected: boolean
}

export default function PreferredTreeToggle({
  'data-testid': dataTestId = 'set-preferred-tree-toggle',
  onToggle,
  isPreferredTreeSelected,
}: Props): JSX.Element {
  const [t] = useTranslation()

  return (
    <Toggle
      data-testid={dataTestId}
      label={t('groups.set-preferred-tree.action')}
      name="preferredTreeToggle"
      checked={isPreferredTreeSelected}
      onChange={onToggle}
    />
  )
}
