import { Button, DialogOverlayActions } from '@fs/zion-ui'
import React, { type ComponentProps } from 'react'
import { useTranslation } from 'react-i18next'

type Props = { addFindFlowResultsPath?: string; handleFunction: ComponentProps<typeof Button>['onClick'] }

export default function MemoriesPrivacyFooter({ handleFunction, addFindFlowResultsPath }: Props): JSX.Element {
  const [t] = useTranslation()
  return (
    <DialogOverlayActions>
      <Button emphasis="medium" onClick={handleFunction} to={addFindFlowResultsPath}>
        {t('common-ui:continue.action')}
      </Button>
    </DialogOverlayActions>
  )
}
