import React from 'react'
import { DialogOverlay, DialogOverlayActions, DialogOverlayFooter, Button } from '@fs/zion-ui'
import { useTranslation } from 'react-i18next'

const RelationshipChangedOverlay = ({ overlay, handleRefreshPOF }) => {
  const { t } = useTranslation()

  return (
    <DialogOverlay {...overlay} title={t('tw.pedigree.relationship-changed.title')}>
      {t('tw.pedigree.relationship-changed.body')}
      <DialogOverlayFooter>
        <DialogOverlayActions>
          <Button onClick={overlay.close}>{t('tw.pedigree.relationship-changed.dismiss')}</Button>
          <Button
            onClick={() => {
              handleRefreshPOF()
              overlay.close()
            }}
            emphasis="high"
          >
            {t('tw.pedigree.relationship-changed.action')}
          </Button>
        </DialogOverlayActions>
      </DialogOverlayFooter>
    </DialogOverlay>
  )
}

export default RelationshipChangedOverlay
