import React, { createContext, useContext, useMemo } from 'react'

const TreeRouteContext = createContext()

export const useTreeRouteContext = () => {
  return useContext(TreeRouteContext)
}

export const TreeRouteProvider = ({ children, isReadOnlyUser }) => {
  const value = useMemo(() => ({ isReadOnlyUser }), [isReadOnlyUser])
  return <TreeRouteContext.Provider value={value}>{children}</TreeRouteContext.Provider>
}
