import { useEffect, useState } from 'react'
import isAuthorizedToViewTree from './isAuthorizedToViewTree'

export default function useIsAuthorizedToViewTree() {
  const [state, setState] = useState({ loading: true })

  useEffect(() => {
    let isMounted = true
    isAuthorizedToViewTree()
      .then((data) => isMounted && setState(data))
      .catch(console.error)

    return () => {
      isMounted = false
    }
  }, [])

  return state
}
