export const hasActivatorFailure = (failures, failureName) => {
  return failures?.some((failure) => failure.dataName === failureName || failure.activator === failureName)
}

export const hasRoleFailure = (roleResults, roleName, name) => {
  if (!roleResults) return false
  const role = roleResults.find(({ roleName: roleResultName }) => roleResultName === roleName)
  if (!role) return false
  const activator = role.activatorResults.find((d) => d.name === name)
  if (!activator) return false
  return !activator.success
}
