import type { ReactElement } from 'react'
import React from 'react'
import { css, cx } from '@linaria/core'
import type { ControlButtonProps } from './ControlButton'
import { elevationBaseCss, e2, h4 } from '../../elevation'

export const groupCss = css`
  display: flex;
  flex-direction: row;
  height: fit-content;
  border-radius: 4px;
  z-index: 1;

  [data-reverse] & {
    flex-direction: row-reverse;
  }
`

export type ControlButtonGroupProps = {
  children: ReactElement<ControlButtonProps>[]
}

const ControlButtonGroup = ({ children }: ControlButtonGroupProps): JSX.Element => (
  <div className={cx(groupCss, elevationBaseCss, e2, h4)}>{children}</div>
)
export default ControlButtonGroup
